import React, { Component } from 'react';
import Modal from 'react-modal';
import img from '../imgs/prod-full.svg';

const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)',
    zIndex                : '9999'
  }
};

Modal.setAppElement('#root')

class ProdutoDetail extends Component {

  constructor(props) {
    super(props);
    this.state = {
      modalIsOpen: false
    };

    this.openModal = this.openModal.bind(this);
    this.afterOpenModal = this.afterOpenModal.bind(this);
    this.closeModal = this.closeModal.bind(this);

  }

  openModal() {
    this.setState({modalIsOpen: true});
  }

  afterOpenModal() {
    // references are now sync'd and can be accessed.
    this.subtitle.style.color = '#f00';
  }

  closeModal() {
    this.setState({modalIsOpen: false});
  }

  addProduto(){
    this.props.handleClick("rudolfo");
    this.openModal();
  }

  render() {
    return (
      <div className="produtoDetail">

        <Modal
            isOpen={this.state.modalIsOpen}
            onAfterOpen={this.afterOpenModal}
            onRequestClose={this.closeModal}
            style={customStyles}
            contentLabel="Example Modal"
            className="Modal"
            overlayClassName="Overlay"
          >
            <img src={img} />
            <button onClick={this.closeModal}>close</button>
            <div>I am a modal</div>
            <form>
              <input />
              <button>tab navigation</button>
              <button>stays</button>
              <button>inside</button>
              <button>the modal</button>
            </form>
          </Modal>

        <h1>Rasteira tira dedo</h1>
        <p className="codProd">RT 0568 | 03.07.0653</p>
        <div className="flex flex-vertical-center">
          <p className="text-danger off-price"><span className="text-through">R$ 69,00</span> |</p>
          <p className="price">R$ 55,22</p>
        </div>
        <p className="parcelas">ou <span>6</span>x de R$<span>9,20</span></p>
        <div className="flex flex-vertical-center">
          <h4>Cor:</h4>
          <span className="fucsia">(Fucsia)</span>
        </div>
        <div className="paleta-cores flex flex-vertical-center">
          <div className="form-control">
            <input checked id="cor-1" name="cor" type="radio" />
            <label for="cor-1">
              <div className="circle">
              </div>
            </label>
          </div>
          <div className="form-control">
            <input id="cor-2" name="cor" type="radio" />
            <label for="cor-2">
              <div className="circle">
              </div>
            </label>
          </div>
        </div>
        <div className="tamanhos flex flex-vertical-center flex-space-between">
          <div className="flex flex-vertical-center">
            <h4>Tamanho:</h4>
            <span className="fucsia">(37)</span>
          </div>
          <a className="link" href="">Guia de medidas</a>
        </div>
        <div className="tamanhos flex flex-vertical-center">
          <div className="form-control">
            <input checked id="tam-1" name="tamanho" type="radio" />
            <label for="tam-1">
              <div className="circle">
              32
              </div>
            </label>
          </div>
          <div className="form-control">
            <input id="tam-2" name="tamanho" type="radio" />
            <label for="tam-2">
              <div className="circle">
              33
              </div>
            </label>
          </div>
        </div>
        <button onClick={this.addProduto.bind(this)} className="btn btn-success btn-large">Adicionar Ã  sacola</button>
        <p className="description">
            Rasteira em atanado soft com tira dedo e fechamento de fivela, possui sola sempre na cor do cabedal
        </p>
      </div>
    );
  }
}

export default ProdutoDetail;
